import { Component } from '@angular/core';
import {FormGroup, Validators,FormBuilder} from '@angular/forms'
import { VerificationService } from './verification.service';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'otp-angular';
  registerForm !: FormGroup;

constructor(private fb:FormBuilder ,private service : VerificationService){
  this.registerForm = this.fb.group({

    number: ['', Validators.compose([Validators.required, Validators.maxLength(10), Validators.pattern('^[0-9]+$')])],
    otp : ['',Validators.required]
  });
}

 public success =0;
  ngOnit(){

  }
  submitnumber(data : any){

    console.log(data)

    this.service.sendotp(data).subscribe((res :any)=>{

      if(res.success == 1){
        alert(res.message);
           this.success = 1;
      }

    })

  }

submitotp(data :any){

       this.service.verifyotp(data).subscribe((res:any)=>{
         if(res.success == 1){
           alert(res.message)
           this.success = 0;
         }
         else if(res.success == 1){
          alert(res.message)
          this.success = 0;
         }
       })
}

}
